
/**
 * Calls callback once the images are loaded :
 * Ex :
     $('img').loaded(function(){
         console.log('done');
     });
 * or
     $('#grid').loaded(function(){
         console.log('done');
     });
 */
$.fn.onImgLoaded = function(callback, maxTime) {
    if(typeof maxTime == 'undefined') {
        maxTime = 15000; // wait MAX
    }
    var $self = this;

    // Get the list of images
    var list      = [],
        listVideo = [];

    if($self.is('img')) {
        list = $self;
    } else if($self.is('video[poster]')) {
        listVideo = $self;
    } else {
        list      = $self.find('img');
        listVideo = $self.find('video[poster]');

        if(!list.length && listVideo.length) {
            callback.call($self);
        }
    }
    var i = 0,
        n = (list.length + listVideo.length)-1;
    var timer = {},
        count = {};

    function checkStatus(c, img, item) {
        if(img.complete) {
            imageLoaded.call(item, c, 'complete');
        } else {
            $(img).one('load', imageLoaded.bind(item, c, 'load'));
            $(img).one('error', imageLoaded.bind(item, c, 'error'));
            if(maxTime) {
                timer[c] = setTimeout(imageLoaded.bind(item, c, 'timeout'), maxTime);
            }
        }
    }
    function imageLoaded(c, status) {
        clearTimeout(timer[c]);

        if(!count[c]) {
            if(status == 'load' || status == 'complete') {
                this.setAttribute('data-loaded', true);
            }
            count[c] = 1;
            i++;
            if(i == n) {
                setTimeout(function(){
                    callback.call($self);
                }, 200);
            }
        }
    }
    list.each(function(c){
        checkStatus(c, this, this);
    });
    listVideo.each(function(c){
        var img = new Image();
        img.src = this.getAttribute('poster');
        checkStatus('vid'+c, img, this);
    });
};

var ScrollPaginate = {
    last_scroll: 0,
    is_loading: 0,
    stop: 0,

    countPage: 0,
    maxPaging: 10,

    container    : false,
    elems        : false,

    loadingMsg   : '',
    navSelector  : '#page-nav',    // selector for the paged navigation
    nextSelector : '#page-nav a',  // selector for the NEXT link (to page 2)
    itemSelector : '.item',        // selector for all items you'll retrieve

    // Update the url when scrolling based on data-page-url
    init: function(container, selector) {
        $(ScrollPaginate.navSelector).hide();

        ScrollPaginate.container = container;
        ScrollPaginate.elems     = selector;

        // Preload loading img
        var img = 'data:image/gif;base64,';
        (new Image()).src = img;

        // Set loading message
        ScrollPaginate.loadingMsg = $('<div id="infscr-loading">'
                                        + '<img alt="Loading..." src="' + img + '" />'
                                        + '<div><em>Loading the next set of posts...</em></div>'
                                    + '</div>').hide().appendTo(document.body);

        // Bind events
        $(window).bind('scroll.infinitescroll', ScrollPaginate.paginate);
        ScrollPaginate.loadFollowing();
    },

    end: function(end) {
        if(typeof end != 'undefined' && end) {
            ScrollPaginate.container.after('<p style="float: right">You\'ve reached the end of the internet!</p>');
        }
        ScrollPaginate.stop = 1;
        $(window).unbind('scroll.infinitescroll');
    },

    paginate: function() {
        if(ScrollPaginate.stop) {
            return;
        }
        var scroll_pos = $(window).scrollTop();

        // Handle scroll events to update content
        if (scroll_pos >= 0.9*($(document).height() - $(window).height())) {
            if (ScrollPaginate.is_loading==0) {
                ScrollPaginate.is_loading = 1;
                ScrollPaginate.loadFollowing();
            }
        }
        /*if (scroll_pos <= 160) {
            if (ScrollPaginate.is_loading==0) {
                ScrollPaginate.is_loading = 1;
                ScrollPaginate.loadPrevious();
            }
        }*/

        // Adjust the URL based on the top item shown
        if (Math.abs(scroll_pos - ScrollPaginate.last_scroll) > $(window).height()*0.1) {
            ScrollPaginate.last_scroll = scroll_pos;

            $(ScrollPaginate.elems).each(function(index) {
                if (!ScrollPaginate.mostlyVisible(this)) {
                    return;
                }
                history.replaceState(null, null, $(this).attr("data-page-url"));
                return(false);
            });
        }
    },

    loadFollowing: function() {

        // We've reached the end of the internet ?
        var $next = $(ScrollPaginate.nextSelector);
        if(!$next.length) {
            ScrollPaginate.end(true);
            return;
        }

        // Query the next page
        ScrollPaginate.loadingMsg.show();

        var href = $next.attr('href');
        $.ajax({
            url: href,
            dataType: 'html',
            success: function(html) {
                ScrollPaginate.countPage++;

                // Append items
                var dom   = $('<div>' + html + '</div>'),
                    items = dom.find(ScrollPaginate.itemSelector);

                if(items.length == 0) {
                    ScrollPaginate.end(true);
                    ScrollPaginate.loadingMsg.hide();
                } else {
                    items = $(items).appendTo(ScrollPaginate.container);
                    ScrollPaginate.appended(items);

                    // Next pagination
                    var nav = dom.find(ScrollPaginate.navSelector);
                    if(!nav.length) {
                        ScrollPaginate.end(true);
                    } else {
                        $(ScrollPaginate.navSelector).replaceWith(nav);
                    }
                }

                // Reset state
                ScrollPaginate.is_loading = 0;
            },
            error: function(xhr) {
                console.error(xhr);
                ScrollPaginate.end();
                ScrollPaginate.loadingMsg.hide();
            }
        });
    },

    // Action when elems have been appended
    complete: function() {

        // Trigger infinitescroll to fill the page
        ScrollPaginate.paginate();

        // Limit the number of images we can show in a single page
        if(ScrollPaginate.countPage >= ScrollPaginate.maxPaging) {
            $(ScrollPaginate.navSelector).show();
            ScrollPaginate.end();
        }
    },

    appended: function($elems) {
        var $container = $(ScrollPaginate.container);

        var n = ($elems.length)-1;

        // Check broken links or append
        $elems.onImgLoaded(function(){
            ScrollPaginate.loadingMsg.hide();

            $elems.each(function(i){

                if(!this.firstChild.getAttribute('data-loaded')) {
                    FeedPicture.handleBrokenLink.call(this.firstChild);
                }

                if(i == n) {
                    setTimeout(function(){
                        $container.masonry('appended', $elems);
                        $container.masonry();
                        ScrollPaginate.complete();

                    }.bind(this), 300);
                }
            });
        });
    },

    // Checks if the given element is mostly visible (at least 25%)
    mostlyVisible: function(element) {
        var scroll_pos    = $(window).scrollTop(),
            window_height = $(window).height();

        var el_top        = $(element).offset().top,
            el_height     = $(element).height(),
            el_bottom     = el_top + el_height;

        return ((el_bottom - el_height*0.25 > scroll_pos) && 
              (el_top < (scroll_pos + 0.5*window_height)));
    }
};

var FeedPicture = {
    placeholdImg: false,
    placeholdImgLink: 'http://i.imgur.com/Qijs1ss.png',
    isInit: false,

    /* Bind events */
    init: function() {
        FeedPicture.initFeed();

        $('.grid').on('click', '.js-delete', FeedPicture.delete);
    },

    /* Init Masonry (pinterest-like grid) */
    initFeed: function() {
        var $container = $('.grid');
    
        /* Replace broken images link with default img */
        FeedPicture.placeholdImg = $container.data('err');
        if(FeedPicture.placeholdImg) {
            (new Image()).src = FeedPicture.placeholdImgLink; // preload it
        }
        $('img').on('error', FeedPicture.handleBrokenLink);

        /* Display images once masonry handled them */
        $container.on('layoutComplete', function(e, items){
            for(var i=0; i<items.length; i++) {
                items[i].element.classList.add('on');
            }
            if(!FeedPicture.isInit) {
                FeedPicture.isInit = true;
                FeedPicture.initScroll($container);
            }
        });

        /* Load grid */
        $container.onImgLoaded(function(){
            setTimeout(function(){
                $container.masonry({itemSelector: '.item', fitWidth: true });
            }, 200);
        });
    },

    /* Either remove or replace image with placeholder */
    handleBrokenLink: function() {
        if(FeedPicture.placeholdImg) {
            if(this.nodeName == 'VIDEO') {
                return;
            } else {
                this.setAttribute('src', FeedPicture.placeholdImgLink);
            }
        } else {
            this.parentNode.remove();
        }
    },

    /* Add paging through scroll */
    initScroll: function($container) {
        ScrollPaginate.init($container, '.item');
    },

    /* Delete a picture */
    delete: function() {
       if(!confirm('Are you sure ?')) {
           return;
       }
       var $btn = $(this);
       $.ajax({
           url: '/picture/delete',
           method: 'POST',
           data: {
               id: $btn.data('id')
           },
           success: function(data) {
               $btn.closest('.grid').masonry('remove', $btn.closest('.item')).masonry();
           },
           error: function(xhr) {
               alert('Something went wrong (' + xhr.status + ' ' + xhr.responseText + ')');
           }
       });
    }
};

$(document).ready(function(){
    FeedPicture.init();
});

/** --------------------------------------------------- **/
var EditPicture = {
    url: false,

    // Bind events
    init: function() {
        $('.js-add-picture').on('keyup keypress', function(e){
            var code = e.keyCode ? e.keyCode : e.which;

            // On enter : check image
            if(code == 13) {
                if(e.type == 'keyup') {
                    if(!this.checkValidity()) {
                        this.reportValidity();
                    } else {
                        EditPicture.add();
                    }
                } else if(this.value == EditPicture.url) {
                    return true;
                }

                e.preventDefault();
                e.stopPropagation();
                return false;
            }
        });
        $('.js-add-picture').on('blur', EditPicture.add);
        $('.js-refresh-picture').on('click', EditPicture.add);
    },

    // Display the picture corresponding to the given link
    add: function() {
        var url = $('.js-add-picture').val();

        // Create a new element
        var img = new Image();
        img.onload = function(){
            EditPicture.url = url;

            $('.js-picture').html(img);
            $('.js-submit').removeAttr('disabled');
        };
        img.onerror = function(err){
            $('.js-picture').html('Could not load the given url');
            $('.js-submit').attr('disabled',  'disabled');
        };
        img.src = url;
    }
};

$(document).ready(function(){
    EditPicture.init();
});